DROP DATABASE IF EXISTS friends;

CREATE DATABASE friends
CHARACTER SET utf8
COLLATE utf8_hungarian_ci;

CREATE TABLE friends.episodes (
  id int(11) NOT NULL,
  name varchar(50) NOT NULL,
  seasonId int(11) NOT NULL,
  number int(11) NOT NULL,
  airstamp datetime NOT NULL,
  runtime int(11) NOT NULL,
  img varchar(255) NOT NULL,
  summary varchar(255) NOT NULL,
  PRIMARY KEY (id)
);

CREATE TABLE friends.seasons (
  id int(11) NOT NULL,
  season varchar(50) NOT NULL,
  years varchar(50) NOT NULL,
  PRIMARY KEY (id)
);

ALTER TABLE friends.episodes
ADD CONSTRAINT FK_episodes_seasons_id FOREIGN KEY (seasonId)
REFERENCES friends.seasons (id) ON DELETE NO ACTION ON UPDATE NO ACTION;


SET NAMES 'utf8';

